INSERT INTO categoria (nome, descricao, num_estante) VALUES
('Autoajuda',           'Livros com dicas e reflexões para crescimento pessoal e bem-estar.',                                1),
('Biografia',           'Relatos sobre a vida de pessoas reais, famosas ou inspiradoras.',                                   2),
('Clássico',            'Obras consagradas pela crítica e pelo tempo, influentes na literatura.',                            3),
('Distopia',            'Narrativas sobre sociedades futuristas ou alternativas com cenários opressivos.',                   4),
('Drama',               'Histórias emocionantes, focadas em conflitos humanos e relações intensas.',                         5),
('Espiritualidade',     'Livros sobre fé, meditação, filosofia espiritual e autoconhecimento.',                              6),
('Existencialismo',     'Obras que exploram questões filosóficas sobre a existência e o sentido da vida.',                   7),
('Fantasia',            'Histórias com elementos mágicos, mundos fictícios e seres sobrenaturais.',                          8),
('Filosofia',           'Reflexões sobre temas fundamentais da existência, ética e conhecimento.',                           9),
('Histórico',           'Narrativas ambientadas em períodos específicos da história, muitas vezes baseadas em fatos reais.', 10),
('Literatura Bras.',    'Obras escritas por autores brasileiros, abordando temas culturais e sociais do país.',              11),
('Literatura Infantil', 'Livros voltados para crianças, com linguagem acessível e mensagens educativas.',                    12),
('Mistério',            'Histórias de investigação, segredos e enigmas a serem desvendados.',                                13),
('Política',            'Livros que analisam sistemas políticos, governos e ideologias.',                                    14),
('Romance',             'Narrativas focadas em relações amorosas e emocionais.',                                             15),
('Romance Juvenil',     'Histórias voltadas para o público jovem, com temas de amadurecimento e descoberta.',                16),
('Suspense',            'Enredos tensos e envolventes, muitas vezes com crimes ou reviravoltas inesperadas.',                17),
('Ficção Científica',   'Histórias sobre tecnologia, espaço, futuro e avanços científicos.',                                 18),
('Horror',              'Narrativas voltadas para o medo, o sobrenatural e o terror psicológico.',                           19),
('Poesia',              'Obras escritas em verso, explorando emoções, imagens e ritmos.',                                    20);
